const text = $input.first().json.content;
const chunkSize = 1000;

let chunks = [];
for (let i = 0; i < text.length; i += chunkSize) {
  chunks.push({
    json: { chunk: text.slice(i, i + chunkSize) }
  });
}

return chunks;

