// R√©cup√®re l'email et les labels
const emailData = $('Get Email Content').first().json;
const allLabels = $('Get Gmail Labels').all().map(item => item.json);

// Filtre les labels syst√®me
const systemLabels = ['INBOX', 'SENT', 'DRAFT', 'SPAM', 'TRASH', 'IMPORTANT', 'STARRED'];
const userLabels = allLabels.filter(label => 
  !systemLabels.includes(label.name.toUpperCase())
);

// Cr√©er la liste des labels format√©e
const labelsList = userLabels.map(label => `- ${label.name}`).join('\n');

// Pr√©parer le contenu de l'email
const emailContent = emailData.text || emailData.body || emailData.snippet || 'Pas de contenu disponible';

// Cr√©er le prompt final MULTI-LABELS
const prompt = `Analyse cet email et choisis les MEILLEURS LABELS (1 √† 3 maximum) parmi ceux disponibles :

EMAIL √Ä ANALYSER :
De: ${emailData.from}
