// Get LLM Chain response
const llmResponse = items[0].json;

// Get labels from Prepare Prompt node
const preparePromptData = $('Prepare Prompt').first().json;
const allLabels = preparePromptData.labels || [];

console.log('ü§ñ LLM Response Raw:', llmResponse);
console.log('üè∑Ô∏è All Labels:', allLabels.map(l => l.name));

// Extract response content (different possible properties)
let responseText = '';
if (llmResponse.text) {
  responseText = llmResponse.text;
} else if (llmResponse.response) {
  responseText = llmResponse.response;
} else if (llmResponse.output) {
  responseText = llmResponse.output;
} else if (typeof llmResponse === 'string') {
  responseText = llmResponse;
} else {
  responseText = JSON.stringify(llmResponse);
}

console.log('üìù Response Text:', responseText);

// Extract label names from response
let labelNames = [];

// Try to parse JSON first
try {
  const parsed = JSON.parse(responseText);
  if (parsed.labels && Array.isArray(parsed.labels)) {
    labelNames = parsed.labels.map(label => label.trim()).filter(label => label.length > 0);
  } else if (parsed.label) {
    // Fallback for old single label format
    labelNames = [parsed.label.trim()];
  }
} catch (e) {
  // If not JSON, look for pattern {"labels": [...]}
  const jsonMatch = responseText.match(/\{[^}]*"labels"[^}]*\[([^\]]+)\][^}]*\}/);
  if (jsonMatch) {
    const labelsString = jsonMatch[1];
    labelNames = labelsString.split(',').map(label => 
      label.replace(/["']/g, '').trim()
    ).filter(label => label.length > 0);
  } else {
    // Fallback: look for old single label format
    const singleMatch = responseText.match(/\{[^}]*"label"[^}]*"([^"]+)"[^}]*\}/);
    if (singleMatch) {
      labelNames = [singleMatch[1].trim()];
    } else {
      // Last fallback: take first mentioned labels
      for (const label of allLabels.slice(0, 3)) {
        if (responseText.toLowerCase().includes(label.name.toLowerCase())) {
          labelNames.push(label.name);
        }
      }
    }
  }
}

// Limit to 3 labels max
labelNames = labelNames.slice(0, 3);

console.log('üéØ Extracted Label Names:', labelNames);

// Find corresponding labels
let selectedLabels = [];

for (const labelName of labelNames) {
  // Exact match
  let foundLabel = allLabels.find(label => 
    label.name.toLowerCase() === labelName.toLowerCase()
  );

  // Fallback: partial match
  if (!foundLabel) {
    foundLabel = allLabels.find(label => 
      label.name.toLowerCase().includes(labelName.toLowerCase()) ||
      labelName.toLowerCase().includes(label.name.toLowerCase())
    );
  }

  if (foundLabel && !selectedLabels.find(l => l.id === foundLabel.id)) {
    // Avoid duplicates
    selectedLabels.push(foundLabel);
  }
}

// If no label found, fallback to first non-system label
if (selectedLabels.length === 0) {
  const systemLabels = ['INBOX', 'SENT', 'DRAFT', 'SPAM', 'TRASH', 'IMPORTANT', 'STARRED'];
  const fallbackLabel = allLabels.find(label => 
    !systemLabels.includes(label.name.toUpperCase())
  ) || allLabels[0];

  if (fallbackLabel) {
    selectedLabels = [fallbackLabel];
  }
}

if (selectedLabels.length === 0) {
  console.error('‚ùå No labels found!');
  return [{ json: { error: 'No labels found' } }];
}

console.log('‚úÖ Final Selected Labels:', selectedLabels.map(l => `${l.name} (${l.id})`));

// Return label IDs for application
const labelIds = selectedLabels.map(label => label.id);

return [{
  json: {
    labelIds: labelIds,
    labels: selectedLabels,
    labelNames: selectedLabels.map(l => l.name),
    llmResponse: responseText,
    extractedLabelNames: labelNames,
    count: selectedLabels.length
  }
}];
