const simplifiedItem = {
  id: $json?.id || null,
  url: $json?.url || null,
  first_name: $json?.first_name || null,
  last_name: $json?.last_name || null,
  headline: $json?.headline || null,
  position: $json?.experiences?.[0]?.position || null,
  position_description: $json?.experiences?.[0]?.description || null,
  company_website: $('Extract Domain').item.json.message.content.domain || null,
  skills: $json?.skills || []
};
return simplifiedItem;

// Function to clean names by removing accents and special characters
const cleanName = (str) => {
  return str.toLowerCase()
    .normalize("NFD").replace(/[\u0300-\u036f]/g, "") // Remove accents
    .replace(/[^a-z0-9]/g, ''); // Remove special characters
};

// Clean the first and last name from the input JSON
const firstName = cleanName($json.first_name);
const lastName = cleanName($json.last_name);

// Method 1 - Get the domain from the OpenAI3 node
// This accesses the domain property from the previous node's output
const domain = $('Extract Domain').item.json.message.content.domain;

// Generate various potential email formats using the first name, last name and domain
const emailFormats = [
  `${firstName}.${lastName}@${domain}`, // format: firstname.lastname@domain
  `${firstName}${lastName}@${domain}`,  // format: firstnamelastname@domain
  `${firstName}@${domain}`,             // format: firstname@domain
  `${firstName[0]}.${lastName}@${domain}`, // format: f.lastname@domain
  `${firstName[0]}${lastName}@${domain}`,  // format: flastname@domain
];

// Return a new object containing all original properties 
// plus the potential emails and extracted domain
return {
  ...$json,  // Keep all properties from the original item
  potential_emails: emailFormats,    // Add the generated email formats
  extracted_domain: domain           // Add the extracted domain for debugging purposes
};
