additionalFields.outputSchema:
{
  "type": "object",
  "properties": {
    "name": {
      "type": "string",
      "description": "The full name of the person"
    },
    "headline": {
      "type": "string",
      "description": "The professional headline"
    },
    "location": {
      "type": "string",
      "description": "The location of the person"
    },
    "current_company": {
      "type": "string",
      "description": "The current company the person works for"
    },
    "current_position": {
      "type": "string",
      "description": "The current position at the company"
    },
    "about": {
      "type": "string",
      "description": "The about section text"
    },
    "experience": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "company_name": {
            "type": "string",
            "description": "The name of the company"
          },
          "position": {
            "type": "string",
            "description": "The position held"
          },
          "duration": {
            "type": "string",
            "description": "The time period worked at the company"
          },
          "location": {
            "type": "string",
            "description": "The location of the job"
          },
          "description": {
            "type": "string",
            "description": "The description of the role"
          }
        },
        "required": [
          "company_name",
          "position",
          "duration",
          "location",
          "description"
        ],
        "additionalProperties": false
      }
    },
    "education": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "institution": {
            "type": "string",
            "description": "The name of the educational institution"
          },
          "degree": {
            "type": "string",
            "description": "The degree obtained"
          },
          "field_of_study": {
            "type": "string",
            "description": "The field of study"
          },
          "duration": {
            "type": "string",
            "description": "The time period of education"
          },
          "description": {
            "type": "string",
            "description": "Any additional information about the education"
          }
        },
        "required": [
          "institution",
          "degree",
          "field_of_study",
          "duration",
          "description"
        ],
        "additionalProperties": false
      }
    },
    "skills": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "List of skills"
    },
    "certifications": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "description": "The name of the certification"
          },
          "issuing_organization": {
            "type": "string",
            "description": "The organization that issued the certification"
          },
          "issue_date": {
            "type": "string",
            "description": "When the certification was issued"
          },
          "expiration_date": {
            "type": [
              "string",
              "null"
            ],
            "description": "When the certification expires (if applicable)"
          }
        },
        "required": [
          "name",
          "issuing_organization",
          "issue_date",
          "expiration_date"
        ],
        "additionalProperties": false
      }
    },
    "languages": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "language": {
            "type": "string",
            "description": "The language"
          },
          "proficiency": {
            "type": "string",
            "description": "The proficiency level"
          }
        },
        "required": [
          "language",
          "proficiency"
        ],
        "additionalProperties": false
      },
      "description": "List of languages and proficiency levels"
    },
    "connections": {
      "type": "integer",
      "description": "The number of connections"
    },
    "recommendations": {
      "type": "integer",
      "description": "The number of recommendations received"
    }
  },
  "required": [
    "name",
    "headline",
    "location",
    "current_company",
    "current_position",
    "about",
    "experience",
    "education",
    "skills",
    "certifications",
    "languages",
    "connections",
    "recommendations"
  ],
  "additionalProperties": false,
  "$schema": "http://json-schema.org/draft-07/schema#"
}
