// Sales Opportunity Scoring Engine
// Helper function for recommendations (moved to top for better organization)
function generateRecommendations(score, signals) {
  const recommendations = [];

  if (score >= 70) {
    recommendations.push('üìû Schedule immediate follow-up call');
    recommendations.push('üë§ Loop in senior sales rep');
    recommendations.push('üìä Prepare custom pricing proposal');
  } else if (score >= 40) {
    recommendations.push('üìß Send detailed product information');
    recommendations.push('üìÖ Offer demo scheduling link');
    recommendations.push('üìö Share relevant case studies');
  } else if (score < 0) {
    recommendations.push('üéØ Address specific objections');
    recommendations.push('üí° Focus on value proposition');
    recommendations.push('üìû Schedule follow-up for better timing');
  }

  if (signals.includes('üèÅ Competitor Evaluation')) {
    recommendations.push('‚öîÔ∏è Prepare competitive differentiation materials');
  }

  return recommendations;
}

try {
  // Safely extract input data with validation
  const input = $input.first();
  if (!input || !input.json || !input.json.body) {
    throw new Error('Invalid input structure');
  }

  const messageData = input.json.body;
  const message = messageData.message;
  const callId = messageData.call_id || 'unknown';
  const userName = messageData.call_data?.userName || 'Unknown User';

  // Validate message structure
  if (!message || !message.message) {
    throw new Error('Message content is missing');
  }

  // Initialize scoring
  let opportunityScore = 0;
  const detectedSignals = [];
  const alerts = [];

  // Buying Signal Detection with Scoring
  const buyingSignals = {
    high_intent: {
      keywords: ['purchase', 'buy', 'implement', 'contract', 'agreement', 'sign up', 'ready to move forward'],
      score: 30,
      label: 'üî• High Purchase Intent'
    },
    decision_maker: {
      keywords: ['decision maker', 'my team', 'our company', 'we need', 'our budget', 'my boss', 'approval from'],
      score: 25,
      label: 'üëî Decision Maker Indicator'
    },
    demo_request: {
      keywords: ['demo', 'trial', 'test', 'try it', 'see it in action', 'show me', 'walk through'],
      score: 25,
      label: 'üñ•Ô∏è Demo Request'
    },
    timeline: {
      keywords: ['when can', 'how soon', 'timeline', 'start date', 'implement by', 'deadline', 'need this by'],
      score: 20,
      label: '‚è∞ Timeline Discussion'
    },
    budget: {
      keywords: ['budget', 'cost', 'pricing', 'investment', 'roi', 'pay', 'afford', 'price point'],
      score: 15,
      label: 'üí∞ Budget Discussion'
    },
    interest: {
      keywords: ['interested', 'tell me more', 'sounds good', 'like this', 'impressive', 'exactly what', 'perfect'],
      score: 15,
      label: 'üëç Positive Interest'
    },
    problem_fit: {
      keywords: ['solve our problem', 'exactly what we need', 'perfect for us', 'this would help', 'addresses our'],
      score: 20,
      label: '‚úÖ Strong Problem-Solution Fit'
    }
  };

  // Negative Signal Detection
  const negativeSignals = {
    price_objection: {
      keywords: ['too expensive', 'cant afford', 'over budget', 'too much', 'cheaper option', 'cost too high'],
      score: -25,
      label: 'üí∏ Price Objection'
    },
    not_interested: {
      keywords: ['not interested', 'no thanks', 'not for us', 'pass', 'not looking', 'don\'t need'],
      score: -30,
      label: '‚ùå Not Interested'
    },
    timing: {
      keywords: ['not right now', 'maybe later', 'next year', 'not a priority', 'down the road', 'future'],
      score: -15,
      label: '‚è∏Ô∏è Bad Timing'
    },
    just_browsing: {
      keywords: ['just looking', 'researching', 'comparing options', 'early stages', 'exploring', 'window shopping'],
      score: -10,
      label: 'üîç Just Browsing'
    }
  };

  // Competitor Detection 
  const competitors = [
    'competitor', 'alternative', 'other options', 'shopping around',
    'comparing', 'versus', 'instead of', 'switch from',
 // Competitor Detection - Add your specific competitors here
   'competitor1', 'competitor2'
  ];

  // Deal Size Indicators
  const dealSizeIndicators = {
    enterprise: {
      keywords: ['enterprise', 'company-wide', 'global', 'multiple teams', 'thousand users', 'all departments', 'entire organization'],
      multiplier: 10,
      label: 'üè¢ Enterprise Deal'
    },
    mid_market: {
      keywords: ['department', 'team of', 'hundred users', 'multiple locations', 'division', 'several teams'],
      multiplier: 5,
      label: 'üè¨ Mid-Market Deal'
    },
    small_business: {
      keywords: ['small team', 'few users', 'startup', 'small business', 'just us', 'handful of people'],
      multiplier: 1,
      label: 'üè™ Small Business Deal'
    }
  };

  // Process message through scoring engine
  const messageLower = message.message.toLowerCase();

  // Check buying signals
  Object.entries(buyingSignals).forEach(([key, signal]) => {
    if (signal.keywords.some(keyword => messageLower.includes(keyword))) {
      opportunityScore += signal.score;
      detectedSignals.push(signal.label);
    }
  });

  // Check negative signals
  Object.entries(negativeSignals).forEach(([key, signal]) => {
    if (signal.keywords.some(keyword => messageLower.includes(keyword))) {
      opportunityScore += signal.score; // Note: score is negative
      detectedSignals.push(signal.label);
      alerts.push(`‚ö†Ô∏è Objection Detected: ${signal.label}`);
    }
  });

  // Check for competitors
  const mentionedCompetitors = competitors.filter(comp => 
    messageLower.includes(comp.toLowerCase())
  );
  if (mentionedCompetitors.length > 0) {
    alerts.push(`üéØ Competitor Mentioned: ${mentionedCompetitors.join(', ')}`);
    detectedSignals.push('üèÅ Competitor Evaluation');
  }

  // Estimate deal size
  let dealSizeEstimate = 'standard';
  let dealMultiplier = 2;
  Object.entries(dealSizeIndicators).forEach(([size, indicator]) => {
    if (indicator.keywords.some(keyword => messageLower.includes(keyword))) {
      dealSizeEstimate = size;
      dealMultiplier = indicator.multiplier;
      detectedSignals.push(indicator.label);
    }
  });

  // Determine lead temperature
  let leadTemperature = 'üßä Cold';
  let alertUrgency = 'low';
  if (opportunityScore >= 70) {
    leadTemperature = 'üî• Hot';
    alertUrgency = 'high';
    alerts.push('üö® HOT LEAD DETECTED!');
  } else if (opportunityScore >= 40) {
    leadTemperature = 'üå°Ô∏è Warm';
    alertUrgency = 'medium';
  } else if (opportunityScore >= 20) {
    leadTemperature = 'üòé Cool';
    alertUrgency = 'low';
  }

  // Extract numbers for deal size estimation - Improved regex
  const numberMatches = messageLower.match(/\$?\d{1,3}(?:,\d{3})*(?:\.\d{2})?[km]?|\d+[km]/gi);
  let estimatedValue = null;
  if (numberMatches && numberMatches.length > 0) {
    // Convert to standardized number
    const lastNumber = numberMatches[numberMatches.length - 1];
    let parsedValue = 0;

    if (lastNumber.toLowerCase().includes('k')) {
      parsedValue = parseFloat(lastNumber.replace(/[^0-9.]/g, '')) * 1000;
    } else if (lastNumber.toLowerCase().includes('m')) {
      parsedValue = parseFloat(lastNumber.replace(/[^0-9.]/g, '')) * 1000000;
    } else {
      parsedValue = parseFloat(lastNumber.replace(/[$,]/g, ''));
    }

    // Only set estimatedValue if we got a valid number
    if (!isNaN(parsedValue) && parsedValue > 0) {
      estimatedValue = parsedValue * dealMultiplier;
    }
  }

  // Prepare output
  return [{
    json: {
      // Message context
      callId: callId,
      userName: userName,
      timestamp: message.sent_at || new Date().toISOString(),
      sender: message.sender || 'unknown',
      message: message.message,

      // Scoring results
      opportunityScore: opportunityScore,
      leadTemperature: leadTemperature,
      alertUrgency: alertUrgency,

      // Detected signals
      detectedSignals: detectedSignals,
      alerts: alerts,

      // Competitor intelligence
      competitorsMentioned: mentionedCompetitors,
      hasCompetitorMention: mentionedCompetitors.length > 0,

      // Deal estimation
      dealSizeCategory: dealSizeEstimate,
      dealMultiplier: dealMultiplier,
      estimatedDealValue: estimatedValue,
      formattedDealValue: estimatedValue ? `$${estimatedValue.toLocaleString()}` : 'Not detected',

      // Action recommendations
      recommendedActions: generateRecommendations(opportunityScore, detectedSignals),

      // Metadata
      processedAt: new Date().toISOString(),
      workflowExecution: $execution?.id || 'unknown',

      // Success indicator
      success: true,
      error: null
    }
  }];

} catch (error) {
  // Return error response
  return [{
    json: {
      success: false,
      error: error.message,
      callId: 'error',
      userName: 'Unknown',
      timestamp: new Date().toISOString(),
      opportunityScore: 0,
      leadTemperature: '‚ùå Error',
      alertUrgency: 'high',
      alerts: [`üö® Processing Error: ${error.message}`],
      recommendedActions: ['üîß Check input data format', 'üìû Contact technical support'],
      processedAt: new Date().toISOString()
    }
  }];
}

