// Process end-of-call summary with final scoring
const callData = $input.first().json.body;
const messages = callData.messages || [];

// Aggregate scoring across entire conversation
let totalScore = 0;
const allSignals = new Set();
const competitorMentions = new Set();
let maxDealValue = 0;
let userMessageCount = 0;

// Process each message
messages.forEach(msg => {
  if (msg.sender === 'user') {
    userMessageCount++;
    const msgLower = msg.message.toLowerCase();

    // Check buying signals
    const buyingKeywords = [
      { keyword: 'interested', score: 15 },
      { keyword: 'budget', score: 15 },
      { keyword: 'timeline', score: 20 },
      { keyword: 'demo', score: 25 },
      { keyword: 'trial', score: 25 },
      { keyword: 'purchase', score: 30 },
      { keyword: 'implement', score: 30 },
      { keyword: 'decision maker', score: 25 },
      { keyword: 'our team', score: 20 }
    ];

    buyingKeywords.forEach(item => {
      if (msgLower.includes(item.keyword)) {
        totalScore += item.score;
        allSignals.add(`‚úÖ ${item.keyword}`);
      }
    });

    // Check negative signals
    const negativeKeywords = [
      { keyword: 'not interested', score: -30 },
      { keyword: 'too expensive', score: -25 },
      { keyword: 'maybe later', score: -15 },
      { keyword: 'just looking', score: -10 }
    ];

    negativeKeywords.forEach(item => {
      if (msgLower.includes(item.keyword)) {
        totalScore += item.score;
        allSignals.add(`‚ùå ${item.keyword}`);
      }
    });

    // Check competitors
    const competitors = ['competitor', 'alternative', 'versus', 'comparing'];
    competitors.forEach(comp => {
      if (msgLower.includes(comp)) {
        competitorMentions.add(comp);
      }
    });
  }
});

// Calculate final metrics
const avgMessageScore = userMessageCount > 0 ? totalScore / userMessageCount : 0;
const qualificationLevel = 
  totalScore >= 100 ? 'üèÜ Highly Qualified' :
  totalScore >= 50 ? '‚úÖ Qualified' :
  totalScore >= 20 ? 'ü§î Needs Nurturing' :
  '‚ùÑÔ∏è Not Qualified';

return [{
  json: {
    callId: callData.call_id,
    userName: callData.call_data.userName,
    duration: callData.evaluation?.duration_minutes || 0,
    totalMessages: messages.length,
    userMessages: userMessageCount,
    finalScore: totalScore,
    averageScore: avgMessageScore.toFixed(1),
    qualificationLevel: qualificationLevel,
    uniqueSignals: Array.from(allSignals),
    competitorsMentioned: Array.from(competitorMentions),
    callSummary: {
      topic: callData.evaluation?.topic || 'General Discussion',
      sentiment: callData.evaluation?.user_sentiment || 'neutral',
      endTime: callData.call_data.endedAt
    },
    recommendations: generateFinalRecommendations(totalScore, allSignals),
    nextBestAction: totalScore >= 50 ? 'üìû Call within 24 hours' : 'üìß Add to nurture campaign'
  }
}];

function generateFinalRecommendations(score, signals) {
  const recs = [];
  if (score >= 100) {
    recs.push('üéØ Fast-track to sales manager');
    recs.push('üìÑ Prepare contract immediately');
    recs.push('üí∞ Custom pricing proposal needed');
  } else if (score >= 50) {
    recs.push('üìß Send personalized follow-up within 2 hours');
    recs.push('üìÖ Schedule next meeting this week');
    recs.push('üìä Prepare ROI calculation');
  } else {
    recs.push('üå± Add to nurture campaign');
    recs.push('üìö Send educational content');
    recs.push('‚è∞ Check back in 30 days');
  }
  return recs;
}

