const items = $input.all();
const updatedItems = items.map(item => {
  const content = item.json.message.content || "";

  // Prepare variables
  let linkedin_url = "";
  let first_name   = "";
  let last_name    = "";
  let domain_name  = "";
  let meaningful_context = "";

  const jsonMatch = content.match(/{[\s\S]*}/);

  if (jsonMatch) {
    // 1️⃣ Try to parse as JSON
    try {
      const data = JSON.parse(jsonMatch[0]);
      linkedin_url        = data.linkedin_url        || "";
      first_name          = data.first_name          || "";
      last_name           = data.last_name           || "";
      domain_name         = data.domain_name         || "";
      meaningful_context  = data.meaningful_context  || "";
    } catch (e) {
      // If JSON parsing fails, fallback to regex extraction
      const urlMatch      = content.match(/linkedin_url:\s*(\S+)/i);
      const firstMatch    = content.match(/first_name:\s*(\S+)/i);
      const lastMatch     = content.match(/last_name:\s*(\S+)/i);
      const domainMatch   = content.match(/domain_name:\s*(\S+)/i);
      const contextMatch  = content.match(/meaningful_context:\s*(.+)/i);

      linkedin_url        = urlMatch      ? urlMatch[1].trim()      : "";
      first_name          = firstMatch    ? firstMatch[1].trim()    : "";
      last_name           = lastMatch     ? lastMatch[1].trim()     : "";
      domain_name         = domainMatch   ? domainMatch[1].trim()   : "";
      meaningful_context  = contextMatch  ? contextMatch[1].trim()  : "";
    }
  } else {
    // 2️⃣ If no JSON found, fallback to regex extraction
    const urlMatch      = content.match(/linkedin_url:\s*(\S+)/i);
    const firstMatch    = content.match(/first_name:\s*(\S+)/i);
    const lastMatch     = content.match(/last_name:\s*(\S+)/i);
    const domainMatch   = content.match(/domain_name:\s*(\S+)/i);
    const contextMatch  = content.match(/meaningful_context:\s*(.+)/i);

    linkedin_url        = urlMatch      ? urlMatch[1].trim()      : "";
    first_name          = firstMatch    ? firstMatch[1].trim()    : "";
    last_name           = lastMatch     ? lastMatch[1].trim()     : "";
    domain_name         = domainMatch   ? domainMatch[1].trim()   : "";
    meaningful_context  = contextMatch  ? contextMatch[1].trim()  : "";
  }

  // Store back on the item
  item.json.linkedin_url        = linkedin_url;
  item.json.first_name          = first_name;
  item.json.last_name           = last_name;
  item.json.domain_name         = domain_name;
  item.json.meaningful_context  = meaningful_context;

  return item;
});

return updatedItems;

function extractLinkedInProfiles(html) {
    // Decode HTML entities
    html = html.replace(/&/g, '&')
               .replace(/\u003d/g, '=')
               .replace(/\x22/g, '"')
               .replace(/\x26/g, '&')
               .replace(/\x3e/g, '>')
               .replace(/\x3c/g, '<');

    const patterns = [
        /(?:https?:)?\/\/(?:[a-z]{2,}\.)?linkedin\.com\/in\/[a-zA-Z0-9._-]+(?:\/[a-z]{2})?/gi,
        /(?:"url"|url=)(?:[^"&]*?)(?:https?:)?\/\/(?:[a-z]{2,}\.)?linkedin\.com\/in\/[a-zA-Z0-9._-]+(?:\/[a-z]{2})?/gi,
        /"(?:https?:)?\/\/(?:[a-z]{2,}\.)?linkedin\.com\/in\/[a-zA-Z0-9._-]+(?:\/[a-z]{2})?"/gi
    ];

    const urls = new Set();
    const urlPositions = [];

    patterns.forEach(pattern => {
        const matches = html.matchAll(pattern);
        for (const match of matches) {
            let url = match[0];
            url = url.replace(/^"url"|^url=|"$/g, '')
                     .replace(/^['"]|['"]$/g, '')
                     .replace(/\\+/g, '')
                     .trim();
            if (!url.startsWith('http')) {
                url = 'https://' + url.replace(/^\/\//, '');
            }
            if (url.includes('linkedin.com/in/')) {
                url = url.split(/[?#&]/)[0];
                url = url.replace(/\/$/, '');
                if (!urls.has(url)) {
                    urls.add(url);
                    urlPositions.push({ url, index: html.indexOf(url) });
                }
            }
        }
    });

    function stripTags(str) {
        return str.replace(/<[^>]+>/g, ' ').replace(/\s+/g, ' ').trim();
    }

    function extractWorkplaceContext(html, index) {
        const after = html.substring(index, Math.min(html.length, index + 4000));

        // 1. Find <div class="YrbPuc">...</div>
        let contextText = '';
        const yrbMatch = after.match(/<div[^>]+class="[^"]*YrbPuc[^"]*"[^>]*>([\s\S]*?)<\/div>/i);
        if (yrbMatch) {
            contextText += stripTags(yrbMatch[1]) + ' ';
        }

        // 2. Find <div class="VwiC3b ...">...</div>
        const vwiMatch = after.match(/<div[^>]+class="[^"]*VwiC3b[^"]*"[^>]*>([\s\S]*?)<\/div>/i);
        if (vwiMatch) {
            contextText += stripTags(vwiMatch[1]);
        }

        // Fallback: if nothing found, return up to 500 chars after the URL (stripped)
        if (!contextText.trim()) {
            contextText = stripTags(after.substring(0, 500));
        }

        return contextText.trim();
    }

    // Build result array
    const results = urlPositions.map(({ url, index }) => {
        const workplace_context = extractWorkplaceContext(html, index);
        return {
            linkedin_url: url,
            workplace_context
        };
    });

    return results;
}

